<?phpclass Upload extends CI_Controller {    public $view_data = array();    private $upload_config;     public function __construct() {        parent::__construct();    }    public function index() {        $this->load->library('myfunction');        $this->load->helper(array('url', 'form'));        $dir = $_SERVER['DOCUMENT_ROOT'] . ROT_DIR . 'file/uploads/post';        $file = $this->myfunction->getlastfile($dir);        $this->load->view('uploadify_v3', $this->view_data);    }    public function do_upload() {        $this->load->library('upload');        $image_upload_folder = PATH_FOLDER . ROT_DIR . 'file/uploads/product';        if (!file_exists($image_upload_folder)) {            mkdir($image_upload_folder, DIR_WRITE_MODE, true);        }        $this->upload_config = array(            'upload_path' => $image_upload_folder,            'allowed_types' => 'png|jpg|jpeg|bmp|tiff',            'max_size' => 2048,            'remove_space' => TRUE,            'encrypt_name' => TRUE,        );        $this->upload->initialize($this->upload_config);        if (!$this->upload->do_upload()) {            $upload_error = $this->upload->display_errors();            echo json_encode($upload_error);        } else {            $file_info = $this->upload->data();            echo json_encode($file_info);        }    }    public function upload_logo() {        $this->load->library('upload');        $image_upload_folder = PATH_FOLDER . ROT_DIR . 'file/uploads/logo';        if (!file_exists($image_upload_folder)) {            mkdir($image_upload_folder, DIR_WRITE_MODE, true);        }        $this->upload_config = array(            'upload_path' => $image_upload_folder,            'allowed_types' => 'png|jpg|jpeg|bmp|tiff',            'max_size' => 2048,            'remove_space' => TRUE,            'encrypt_name' => TRUE,        );        $this->upload->initialize($this->upload_config);        if (!$this->upload->do_upload()) {            $upload_error = $this->upload->display_errors();            echo json_encode($upload_error);        } else {            $file_info = $this->upload->data();            echo json_encode($file_info);        }    }    public function do_upload_new() {        $this->load->library('upload');        $image_upload_folder = PATH_FOLDER . ROT_DIR . 'file/uploads/new';        if (!file_exists($image_upload_folder)) {            mkdir($image_upload_folder, DIR_WRITE_MODE, true);        }        $this->upload_config = array(            'upload_path' => $image_upload_folder,            'allowed_types' => 'png|jpg|jpeg|bmp|tiff',            'max_size' => 2048,            'remove_space' => TRUE,            'encrypt_name' => TRUE,        );        $this->upload->initialize($this->upload_config);        if (!$this->upload->do_upload()) {            $upload_error = $this->upload->display_errors();            echo json_encode($upload_error);        } else {            $file_info = $this->upload->data();            echo json_encode($file_info);        }    }    public function upload_file(){			$status = "";			$msg = "";			$tit = $this->input->post("title");			if($tit == NULL){				$status = "error";				$msg = "Please enter your title";				}			if($status != "error"){				$config['upload_path'] = '.\upload';				$config['allowed_types'] = 'gif|jpg|png|doc|txt';				$config['max_size']  = 1024 * 8;				$config['encrypt_name'] = TRUE;				$this->load->library("upload",$config);				if(!$this->upload->do_upload("ufile")){					$status = "error";					$msg = $this->upload->display_errors('<p>','</p>');					echo $msg;				}else{										$data = $this->upload->data();					$info = array("file_name" => $data['file_name'],								  "title"     => $_POST['title']);											$status = "Success";						$msg = "File successfully uploaded";										}			}			echo json_encode(array("status"    => $status,								   "msg"   => $msg));	}}