(4, 'Nguyễn Đình Hán', 'Khu phong vượng cốt khí và Khí cốt đan', 'hanmarketings@gmail.com', '<p><span style="font-size:18px"><span style="font-family:georgia,serif">T&ocirc;i muốn hỏi sản phẩm Khu Phong Vượng Cốt Kh&iacute; v&agrave; Kh&iacute; Cốt Đan c&oacute; t&aacute;c dụng giống nhau kh&ocirc;ng?</span></span></p>\n', '<p style="text-align:justify"><span style="color:#0000CD"><span style="font-size:18px"><span style="font-family:georgia,serif">Ch&uacute;ng t&ocirc;i xin trả lời!</span></span></span></p>\n\n<p style="text-align:justify"><span style="color:#0000CD"><span style="font-size:18px"><span style="font-family:georgia,serif">Khu Phong Vượng Cốt Kh&iacute; v&agrave; Kh&iacute; Cốt Đan đều&nbsp;chứa c&aacute;c th&agrave;nh phần giống nhau v&agrave; c&oacute; t&aacute;c dụng giống nhau.Nhưng hai sản phẩm n&agrave;y được b&agrave;o chế ở hai c&aacute;ch kh&aacute;c nhau.</span></span></span></p>\n\n<p style="text-align:justify"><span style="color:#0000CD"><span style="font-size:18px"><span style="font-family:georgia,serif">-&nbsp;Khu Phong Vượng Cốt Kh&iacute; được ng&acirc;m trong rượu v&agrave; d&agrave;nh cho người kh&ocirc;ng bị mắc c&aacute;c bệnh Tai biến,Cao huyết &aacute;p v&agrave; Đau dạ d&agrave;y.</span></span></span></p>\n\n<p style="text-align:justify"><span style="color:#0000CD"><span style="font-size:18px"><span style="font-family:georgia,serif">- Kh&iacute; Cốt Đan&nbsp;được b&agrave;o chế ở dạng vi&ecirc;n ho&agrave;n tễ,d&agrave;nh cho mọi&nbsp;người bệnh.</span></span></span></p>\n', 1, 1396798299),
(6, 'Nguyễn Đình Hán', 'Tác dụng phụ?', 'hanmarketings@gmail.com', '<p><span style="font-size:18px"><span style="font-family:georgia,serif">D&ugrave;ng sản phẩm Khu Phong Vượng Cốt Kh&iacute; v&agrave; Kh&iacute; Cốt Đan&nbsp;c&oacute; t&aacute;c dụng phụ kh&ocirc;ng?</span></span></p>\n', '<p><span style="color:#0000CD"><span style="font-size:18px"><span style="font-family:georgia,serif">Ch&uacute;ng t&ocirc;i xin trả lời!</span></span></span></p>\n\n<p><span style="color:#0000CD"><span style="font-size:18px"><span style="font-family:georgia,serif">D&ugrave;ng thuốc đ&ocirc;ng y kh&ocirc;ng c&oacute; t&aacute;c dụng phụ. Nếu c&oacute; trường hợp đi ngo&agrave;i l&agrave; do thuốc m&aacute;t qu&aacute;, l&uacute;c đ&oacute; bạn gọi điện trực tiếp cho ch&uacute;ng t&ocirc;i để được hướng dẫn cụ thể.</span></span></span></p>\n', 1, 1396799952),
(7, 'Nguyễn Đình Hán', 'Ảnh hưởng đến thận?', 'hanmarketings@gmail.com', '<p><span style="font-size:18px"><span style="font-family:georgia,serif">D&ugrave;ng sản phẩm <span style="background-color:#FFD700">&nbsp;Khu Phong Vượng Cốt Kh&iacute; v&agrave; Kh&iacute; Cốt Đan</span>&nbsp;&nbsp;c&oacute; ảnh hưởng g&igrave; đến gan thận.T&ocirc;i thận yếu uống thuốc đ&ocirc;ng y sợ sẽ kh&ocirc;ng đ&agrave;o thải được?</span></span></p>\n', '<p><span style="color:#0000CD"><span style="font-size:18px"><span style="font-family:georgia,serif">Ch&uacute;ng t&ocirc;i xin trả lời!</span></span></span></p>\n\n<p><span style="color:#0000CD"><span style="font-size:18px"><span style="font-family:georgia,serif">Thuốc đ&ocirc;ng y c&oacute; t&aacute;c dụng m&aacute;t gan, bổ gan, bổ thận kh&ocirc;ng c&oacute; ảnh hưởng xấu đến gan thận như thắc mắc của bạn. Uống thuốc đ&ocirc;ng y hỗ trợ sức khỏe, n&acirc;ng cao thể trạng. Phụ nữ uống thuốc đ&ocirc;ng y gi&uacute;p bổ huyết, giải độc gi&uacute;p khỏe v&agrave; đẹp da. Đ&agrave;n &ocirc;ng uống thuốc bổ thận, n&acirc;ng cao sức khỏe v&agrave; tăng cường khả năng sinh l&yacute;.</span></span></span></p>\n', 1, 1396800394),
(8, 'Nguyễn Đình Hán', 'Tây y và Đông y?', 'hanmarketings@gmail.com', '<p><span style="font-size:18px"><span style="font-family:georgia,serif">T&ocirc;i&nbsp;vừa uống thuốc T&acirc;y y vừa uống thuốc đ&ocirc;ng y.Xin hỏi&nbsp;c&oacute; ảnh hưởng g&igrave; kh&ocirc;ng?</span></span></p>\n', '<p><span style="color:#0000CD"><span style="font-size:18px"><span style="font-family:georgia,serif">Ch&uacute;ng t&ocirc;i xin trả lời!</span></span></span></p>\n\n<p><span style="color:#0000CD"><span style="font-size:18px"><span style="font-family:georgia,serif">Bạn vẫn d&ugrave;ng được nhưng c&aacute;ch nhau v&agrave;i tiếng l&agrave; được .</span></span></span></p>\n', 1, 1396800576);

